    private static final ObjectMapper MAPPER = new ObjectMapper();

    /**
     * Принимает JSON-строку, переносит defaultTextureValue → paintTexture и
     * зануляет defaultTextureValue во всех вложенных объектах.
     *
     * @param rawJson исходный JSON
     * @return модифицированный JSON
     * @throws JsonProcessingException если JSON некорректен
     */
    public static String moveDefaultToPaint(String rawJson) throws JsonProcessingException {
        JsonNode root = MAPPER.readTree(rawJson);
        processNode(root);
        return MAPPER.writeValueAsString(root);
    }

    /** Рекурсивный обход дерева JSON. */
    private static void processNode(JsonNode node) {
        if (node.isObject()) {
            ObjectNode obj = (ObjectNode) node;


            JsonNode tagHead = obj.get("tagHead");
            if (tagHead != null && tagHead.isObject()) {
                ObjectNode tagHeadObj = (ObjectNode) tagHead;
                JsonNode valueNode = tagHeadObj.get("Value");

                if (valueNode != null && !valueNode.isNull()) {

                    obj.set("paintTexture", valueNode);

                    tagHeadObj.putNull("Value");
                }
            }


            obj.fields().forEachRemaining(e -> processNode(e.getValue()));

        } else if (node.isArray()) {
            ArrayNode arr = (ArrayNode) node;
            arr.forEach(PrevGen::processNode);
        }
    }



